    s = list(s)
    n = len(s)
    a += s.count('0')
    b -= s.count('1')
    if a < 0 or b < 0:
        return '-1'
    for i in range(n):
        if s[i] == '?':
            s[i] = s[n - i - 1]
            if s[i] == '0':
                a -= 1
            elif s[i] == '1':
                b -= 1
            else:
                s[i] = '*'
    if a < 0 or b < 0:
        return '-1'
    for i in range(n):
        if i == n // 2 and n % 2:
            continue
        if s[i] == '*':
            if a >= 2:
                s[i] = '0'
                s[n - i - 1] = '0'
                a -= 2
            else:
                s[i] = '1'
                s[n - i - 1] = '1'
                b -= 2
    if s[n // 2] == '*':
        if a > 0:
            s[n // 2] = '0'
            a -= 1
        else:
            s[n // 2] = '1'
            b -= 1
    if a != 0 or b != 0:
        return '-1'
    elif s != s[::-1]:
        return '-1'
    else:
        return ''.join(s)

