import collections
import sys

def find_kth_lexicographic_sequence(n, m, k, edges):
    sys.setrecursionlimit(10 ** 9)

    def dfs(u, ii):
        vis[u] = ii
        for (v, wt) in g[u]:
            if v not in vis:
                x_vals[v] = x_vals[u] ^ wt
                dfs(v, ii)
            elif x_vals[v] != x_vals[u] ^ wt:
                return -1
        return None
    k += 1
    g = collections.defaultdict(list)
    for (x, y, z) in edges:
        g[x].append((y, z))
        g[y].append((x, z))
    vis = {}
    x_vals = [0 for i in range(n + 1)]
    last = ii = 0
    for i in range(1, n + 1):
        if i not in vis:
            result = dfs(i, ii)
            if result == -1:
                return -1
            last = i
            ii += 1
    for i in range(1, n + 1):
        if vis[i] == vis[last]:
            x_vals[i] ^= k
    return x_vals[1:]

