def convert_decimal_to_base(N: int, B: int) -> str:
    """"""
    sum = ''
    hex_dict = {10: 'A', 11: 'B', 12: 'C', 13: 'D', 14: 'E', 15: 'F'}
    while N > 0:
        rem = N % B
        if rem > 9:
            rem_str = hex_dict[rem]
        else:
            rem_str = str(rem)
        sum = rem_str + sum
        N = N // B
    return sum
