from collections import deque

def bfs(visit, graph, level, i):
    visit[i] = 2
    q = deque()
    q.append([i, 0])
    while q:
        t = q.popleft()
        u = t[0]
        lev = t[1]
        if lev not in level:
            level[lev] = [u]
        else:
            level[lev].append(u)
        for v in graph[u]:
            if visit[v] == 0:
                visit[v] = 1
                q.append([v, lev + 1])

def count_even_distance_pairs(graph, n):
    visit = [0] * (n + 1)
    level = {}
    for i in range(1, n + 1):
        if visit[i] == 0:
            bfs(visit, graph, level, i)
    odd = 0
    even = 0
    for i in level:
        if i % 2 == 0:
            even += len(level[i])
        else:
            odd += len(level[i])
    ans = even * (even - 1) // 2 + odd * (odd - 1) // 2
    return ans

