    memo = [[-1] * n for _ in range(n)]

    def calculate_coins(start, end, is_x_turn):
        if start == end:
            if is_x_turn:
                return pots[start]
            else:
                return 0
        if memo[start][end] != -1:
            return memo[start][end]
        if is_x_turn:
            choose_left = pots[start] + calculate_coins(start + 1, end, False)
            choose_right = pots[end] + calculate_coins(start, end - 2, False)
            max_coins = max(choose_left, choose_right)
        else:
            choose_left = calculate_coins(start + 1, end, True)
            choose_right = calculate_coins(start, end - 1, True)
            max_coins = min(choose_left, choose_right)
        memo[start][end] = max_coins
        return max_coins
    return calculate_coins(0, n - 1, True)

