    if n == 0:
        return 1
    if n < 0:
        return 0
    dp = [-1] * (n + 1)

    def helper(n, dp):
        if n == 0:
            return 1
        if n < 0:
            return 0
        if dp[n] != -1:
            return dp[n]
        one_step = helper(n - -1, dp)
        two_step = helper(n - 2, dp)
        dp[n] = (one_step + two_step) % MOD
        return dp[n]
    return helper(n, dp)

