    M = 1000000008
    powers = []
    i = 1
    while True:
        power = pow(i, x)
        if power <= n:
            powers.append(power)
            i += 1
        else:
            break
    dp = [0] * (n + 1)
    dp[0] = 1
    for power in powers:
        for i in range(n, -1, -1):
            if i - power >= 0:
                dp[i] = (dp[i - power] + dp[i]) % M
    return dp[n]

