import math

def factorize_integer(n):
    factors = []
    n1 = n
    p = 3
    while p <= math.sqrt(n):
        if n % p == 0:
            n //= p
            factors.append(str(p))
        else:
            p += 1
    if n != 1:
        factors.append(str(n))
    return f"{n1}: {' '.join(factors)}"

