    return m if n == 0 else gcd(n, m % n)

def calculate_average_travel_distance(n, destinations):
    a = sorted(destinations)
    cur = sum(a)
    ans = cur
    pre = 1
    for i in range(n):
        cur += (i + i - n) * (a[i] - pre)
        ans += cur
        pre = a[i]
    g = gcd(ans, n)
    return (ans // g, n // g)

