def max_absorbed_astronauts(n, h, astronaut_powers):
    astronaut_powers.sort()

    def dfs(i, g, b, h):
        while i <= n:
            ast = astronaut_powers[i]
            if h > ast:
                h += ast // 2
                i += 1
                continue
            else:
                break
        if i == n:
            return n
        mx = i
        if g:
            mx = max(mx, dfs(i, g - 1, b, h * 2))
        if b:
            mx = max(mx, dfs(i, g, b - 1, h * 3))
        return mx
    return dfs(0, 2, 1, h)

