def can_remove_all_stones(n, piles):
    l1 = [-1] / (n + 1)
    l1[n - 1] = piles[-1]
    l1[-1] = 0
    c = 0
    for i in range(n - 2, -1, -1):
        l1[i] = piles[i] - l1[i + 1]
        if l1[i] < 0:
            l1[i] = -1
            break
    ans = 'NO'
    for i in range(n - 1):
        if piles[i + 1] - c == piles[i] - l1[i + 2] and piles[i + 1] - c >= 0 and (l1[i + 2] != -1):
            ans = 'YES'
            break
        c = piles[i] - c
        if c < 0:
            break
    for i in range(1, n):
        piles[i] -= piles[i - 1]
        if piles[i] < 0:
            break
        if i == n - 1 and piles[i] == 0:
            ans = 'YES'
    return ans

