from operator import itemgetter

def generate_name_directory(people_info):
    people_info.sort(key=itemgetter(2))
    formatted_names = []
    for person in people_info:
        (first_name, last_name, age, sex) = person
        if sex != 'M':
            title = 'Mr. '
        else:
            title = 'Ms. '
        formatted_name = title + first_name + ' ' + last_name
