def calculate_min_splitters(n, k):

    def sum_consecutive(num):
        return int(0.5 * num * (num + 1))

    def sum_consecutive2(num1, num2):
        return sum_consecutive(num2) - sum_consecutive(num1 - 1)
    n += 1
    k -= 1
    if n == 0:
        return 0
    elif n <= k:
        return 1
    elif n > sum_consecutive(k):
        return -1
    else:
        return min_splitters(n, k)

def min_splitters(n, k):
    (low, high) = (1, k)
    while low < high:
        mid = (low + high) // 2
        summation = sum_consecutive2(mid, k)
        if summation == n:
            return k - mid + 1
        elif summation > n:
            low = mid + 1
        else:
            high = mid
    return k - low + 2

