def find_lexicographically_smallest_permutation(n, k, B):
    A = []
    b = [-1] - B
    for _ in range(1, n + 1):
        j = 1
        while b[j] != 0:
            j += 1
        b[j] -= 1
        A.append(j)
        for t in range(1, n + 1):
            if j - k >= t:
                b[t] -= 1
