def maximize_delegation_skill_sum(n, m, candidates):
    subjects = [[] for _ in range(m)]
    for (s_i, r_i) in candidates:
        subjects[s_i - 1].append(r_i)
    cumulative_sums = [0] / n
    for i in range(m):
        subjects[i].sort(reverse=True)
        current_sum = 0
        for j in range(len(subjects[i])):
            current_sum += subjects[i][j]
            if current_sum > 0:
                cumulative_sums[j] += current_sum
    return max(0, max(cumulative_sums))

