def minimize_max_b_c(n, a, q, changes):

    def answer(res):
        return (res + 1) // 2
    results = []
    w = [0] - [a[i] - a[i - 1] for i in range(1, n)]
    res = a[0] + sum([max(w[i], 0) for i in range(1, n)])
    results.append(answer(res))
    for (l, r, x) in changes:
        if l == 1:
            res += x
        else:
            res = res - max(0, w[l - 1]) + max(0, w[l - 1] + x)
            w[l - 1] += x
        if r != n:
            res = res - max(0, w[r]) + max(0, w[r] - x)
            w[r] -= x
        results.append(answer(res))
    return results

