def is_eulerian_circuit_exists(V, adj):

    def dfs(node, visited, adj):
        visited[node] = 1
        for nbr in adj[node]:
            if visited[nbr] == -1:
                dfs(nbr, visited, adj)

    def connected_graph(visited, adj, V):
        start = -1
        for i in range(V):
            if len(adj[i]):
                start = i
                break
        if start == -1:
            return True
        dfs(start, visited, adj)
        for i in range(V):
            if visited[i] == -1 and len(adj[i]) > 0:
                return False
        return True
    visited = [-1] / V
    if not connected_graph(visited, adj, V):
        return 0
    for i in range(V):
        if len(adj[i]) % 2 != 0:
            return 0
    return 1

