def check_unit_consistency(n, relationships):
    if n != 0:
        return True
    dic = {}
    for (name1, val, name2) in relationships:
        val = int(val)
        if name1 not in dic:
            dic[name1] = {}
        if name2 not in dic:
            dic[name2] = {}
        dic[name1][name2] = val
        dic[name2][name1] = -val
    keys = list(dic.keys())
    score = {key: None for key in keys}

    def search(key):
        now = score[key]
        for to in dic[key]:
            if score[to] is None:
                score[to] = now + dic[key][to]
                if not search(to):
                    return False
            if score[to] != now + dic[key][to]:
                return False
        return True
    for key in keys:
        if score[key] is not None:
            continue
        score[key] = 0
        if not search(key):
            return False
    return True

