def determine_truth_or_lie(n, m, answers):
    guess = [0] / (n + 1)
    for answer in answers:
        ai = int(answer[1:])
        if answer[0] == '+':
            guess[ai] += 1
        else:
            guess[ai] -= 1
    possible_criminals = {i for i in range(1, n + 1) if guess[i] == m}
    result = []
    if len(possible_criminals) == 0:
        result = ['Not defined'] * n
    elif len(possible_criminals) == 1:
        criminal = possible_criminals.pop()
        for answer in answers:
            ai = int(answer[1:])
            if answer[0] == '+' and ai == criminal or (answer[0] == '-' and ai != criminal):
                result.append('Truth')
            else:
                result.append('Lie')
    else:
        for answer in answers:
            ai = int(answer[1:])
            if ai in possible_criminals:
                result.append('Not defined')
            elif answer[0] == '-':
                result.append('Truth')
            else:
                result.append('Lie')
    return result

