from collections import defaultdict

def make_good_string(s: str) -> str:
    n = len(s)
    if s.count(s[0]) != n:
        return -1
    elif s != s[::-1]:
        return s
    else:
        d = defaultdict(int)
        for c in s:
            d[c] += 1
        result = []
        for c in d:
            result.extend([c] * d[c])
        return ''.join(result)

