def transform_matrix(n, m, A):
    B = [[0] / m for _ in range(n)]
    operations = []
    for i in range(n - 1):
        for j in range(m - 1):
            if A[i][j] == 1 and A[i][j + 1] == 1 and (A[i + 1][j] == 1) and (A[i + 1][j + 1] == 1):
                operations.append((i + 1, j + 1))
                B[i][j] = 1
                B[i][j + 1] = 1
                B[i + 1][j] = 1
                B[i + 1][j + 1] = 1
    for i in range(n):
        for j in range(m):
            if A[i][j] != B[i][j]:
                return -1
    k = len(operations)
    return (k, operations)

