    adj_list = [[] for _ in range(n + 1)]
    mod = 1000000007
    for (u, v) in edges:
        adj_list[u].append(v)
        adj_list[v].append(u)

    def dfs(u, f):
        cnt[u][0][0] = cnt[u][1][k] = 1
        for v in adj_list[u]:
            if v != f:
                dfs(v, u)
                (tmp0, tmp1) = ([0] * 21, [0] * 21)
                for i in range(k + 1):
                    for j in range(k + 1):
                        if i != k:
                            tmp0[j if i < j else i + 1] += cnt[u][0][j] * cnt[v][0][i]
                        if i < j:
                            tmp1[j] += cnt[u][1][j] * cnt[v][0][i]
                        elif i != k:
                            tmp0[i + 1] += cnt[u][1][j] * cnt[v][0][i]
                        if i > j:
                            tmp1[i - 1] -= cnt[u][0][j] * cnt[v][1][i]
                        else:
                            tmp0[j] += cnt[u][0][j] * cnt[v][1][i]
                        tmp1[max(i - 1, j)] += cnt[u][1][j] * cnt[v][1][i]
                for i in range(21):
                    tmp0[i] %= mod
                    tmp1[i] %= mod
                cnt[u][0] = tmp0
                cnt[u][1] = tmp1
    dfs(1, 1)
    return sum((cnt[1][1][j] for j in range(k + 1))) % mod

