def calculate_minimum_removal_time(n, m, a):
    max_not_zero = 1
    for (index, value) in enumerate(a):
        if value > 0:
            max_not_zero = index
    total = sum(a)

    def remove(t):
        boxes = 0
        s = m
        for i in range(max_not_zero + 1):
            boxes += a[i]
            while boxes + (i + 1) >= t:
                boxes -= t - i - 1
                s -= 1
                if s < 0:
                    return False
        if s == 0:
            if boxes > 0:
                return False
        return True

    def binary_search(left, right):
        mid = (left + right) // 2
        if right - left <= 1 and remove(left):
            return left
        if remove(mid):
            return binary_search(left, mid)
        else:
            return binary_search(mid + 1, right)
    return binary_search(2, total + n)

