def max_subset_size_and_ways(N, M):
    MOD = 998244354

    def groupsInInterval(L, R, M, N):
        if R <= N:
            return R - (L - 1) - (R // M - (L - 1) // M)
        else:
            return 0

    def power(a, b):
        res = 1
        while b > 0:
            if b % 2:
                res *= a
                res %= MOD
            b //= 2
            a *= a
            a %= MOD
        return res
    (taken, ways) = (0, 1)
    for i in range(1, 70):
        L = N // M ** i + 1
        R = N // M ** (i - 1)
        groups = groupsInInterval(L, R, M, N)
        if i % 2 == 0:
            taken += i // 2 * groups
            ways *= power(i // 2 + 1, groups)
        else:
            taken += (i + 1) // 2 * groups
            ways *= 1
    return (taken, ways % MOD)

