from math import ceil

def can_hero_kill_all_monsters(A, B, monsters):
    monsters.sort(key=lambda x: x[0])
    for (attack, health) in monsters:
        hits_needed = ceil(health / A)
        B += hits_needed * attack
    if B + monsters[-1][0] > 0:
        return True
    else:
        return False

