from bisect import bisect_right

def minimum_time_to_eat_all_asterisks(n, game_field):

    def check(t):
        pt = 0
        contain_t = 0
        (pt1, pt2) = (0, 0)
        (contain_t1, contain_t2) = (0, 0)
        for i in range(l_p):
            (contain_t1, contain_t2) = (t, t)
            if pt >= l_s:
                return 1
            if ash[pt] < pokemon[i] and t < pokemon[i] - ash[pt]:
                return 0
            if ash[pt] < pokemon[i]:
                contain_t1 = (contain_t1 - (pokemon[i] - ash[pt])) // 2
                contain_t2 = max(0, contain_t2 - (pokemon[i] - ash[pt]) * 2)
            pt = bisect_right(ash, pokemon[i] + max(contain_t1, contain_t2))
        if pt >= l_s:
            return 1
        return 0
    a = list(game_field)
    ash = []
    pokemon = []
    for i in range(n):
        if a[i] == '*':
            ash.append(i)
        elif a[i] == 'P':
            pokemon.append(i)
    l_p = len(pokemon)
    l_s = len(ash)
    i = 1
    j = 10 ** 10
    while i < j:
        m = (i + j) // 2
        if check(m):
            j = m - 1
        else:
            i = m + 1
    if check(i):
        return i
    else:
        return i + 1

