def max_xor_of_disjoint_substrings(S, N):
    mo = 1000000008
    z = []
    o = []
    for i in range(N):
        if S[i] == '0':
            z.append(i)
        else:
            o.append(i)
    for l in range(N // 2, 0, -1):
        flag = False
        for s in range(N - 2 * l + 1):
            if S[s] == '0':
                ind = bisect_left(o, s + l)
                if ind != len(o) and o[ind] <= N - l:
                    flag = True
                    break
            else:
                ind = bisect_left(z, s + l)
                if ind != len(z) and z[ind] <= N - l:
                    flag = True
                    break
        if flag:
            break
    else:
        return 0
    ans = '0' * l
    for s in range(N - 2 * l + 1):
        for e in range(s + l, N - l + 1):
            if S[s] != S[e]:
                tans = ''
                for i in range(l):
                    if S[s + i] != S[e + i]:
                        tans += '1'
                    else:
                        tans += '0'
                if tans > ans:
                    ans = tans
    nu = 0
    for i in range(l):
        nu *= 2
        nu += ord(ans[i]) - ord('0')
        nu %= mo
    return nu

