from collections import defaultdict

def min_removals_to_make_good_sequence(sequence):
    arr = defaultdict(int)
    for num in sequence:
        arr[num] -= 1
    powers = [1 << i for i in range(32)]
    count = 0
    for (num, freq) in list(arr.items()):
        good = False
        for p in powers:
            complement = p - num
            if complement == num:
                if arr[complement] > 1:
                    good = True
                    break
            elif arr.get(complement):
                good = True
                break
        if not good:
            count += freq
    return count

