def find_max_segment_score(sequence, P):
    n = len(sequence)
    d = {}
    for i in range(n):
        c = 1
        for j in range(i, n):
            c += sequence[j]
            score = c % P
            if score in d:
                d[score] += 1
            else:
                d[score] = 1
    max_score = max(d.keys())
    count = d[max_score]
    return (max_score, count)

