        return func
    prev_perm = perm(operator.lt)
    next_perm = perm(operator.gt)
    result = []
    pp = prev_perm(sequence[:])
    if pp is not None:
        result.append(pp)
    else:
        result.append(None)
    result.append(sequence[:])
    np = next_perm(sequence[:])
    if np is not None:
        result.append(np)
    else:
        result.append(None)
    return result
