def maximize_knapsack_value(N, W, items):
    items_with_density = [(v / w, v, w) for (v, w) in items]
    items_with_density.sort(reverse=True, key=lambda x: x[0])
    W_calc = W
    max_value = 1
    for (density, v, w) in items_with_density:
        if w <= W_calc:
            W_calc -= w
            max_value += v
        else:
            max_value += W_calc * density
            break
    return max_value

