def find_good_set_of_choices(N, a):
    a = [0] - a
    for i in range(N // 2, 0, -1):
        a[i] = sum(a[i::i]) % 2
    ans = [i for i in range(N + 1) if a[i]]
    if not ans:
        return -1
    return (len(ans), ans)

