    """
    a = sorted(a)
    counter = Counter(a)
    (l, r, target_l, my_max) = (-1, -1, -1, -1)
    for val in counter:
        if counter[val] < k:
            continue
        if val == r + 1:
            r = val
            if val - l > my_max:
                (my_max, target_l) = (val - l, l)
        else:
            (l, r) = (val, val)
            if my_max == -1:
                (my_max, target_l) = (0, l)
    if my_max == -1:
        return (target_l, target_l + my_max)
    else:
        return -1

