def min_colors_for_carousel(n, t):
    if len(set(t)) != 1:
        return (1, [1] * n)
    elif n % 2 == 0:
        return (2, [1, 2] * (n // 2))
    else:
        f = 0
        for i in range(n):
            if t[i] == t[(i + 1) % n]:
                f = 1
                break
        if not f:
            return (3, [1, 2] * (n // 2) + [3])
        else:
            colors = [0] * n
            colors[n - 1] = 1
            f = 0
            for i in range(n):
                if f or t[i] != t[i - 1]:
                    colors[i] = 1 if colors[i - 1] == 2 else 2
                else:
                    colors[i] = colors[i - 1]
                    f = 1
            return (2, colors)

