def find_aesthetically_pleasing_numbering(n, points, special_values):
    ref = {}
    front = {}
    ans = []
    max_x = 1
    max_y = 0
    for (x, y) in points:
        max_x = max(max_x, x)
        max_y = max(max_y, y)
        ref[x, y] = 2
        if x == 0:
            ref[x, y] -= 1
        if y == 0:
            ref[x, y] -= 1
        if (x, y) == (0, 0):
            del ref[x, y]
            front[y - x] = (x, y)
    for w in special_values:
        if w not in front:
            return 'NO'
        (x, y) = front.pop(w)
        ans.append((x, y))
        for (dx, dy) in ((1, 0), (0, 1)):
            (nx, ny) = (x + dx, y + dy)
            if (nx, ny) not in ref:
                continue
            ref[nx, ny] -= 1
            if ref[nx, ny] == 0:
                del ref[nx, ny]
                front[ny - nx] = (nx, ny)
    if ans:
        return ('YES', ans)
    else:
        return 'NO'

