from math import log
MOD = 10 ** 9 - 7

def int23compare(lst):
    (count2, count3) = lst
    return count2 * log(2) + count3 * log(3)

def calculate_max_intimidation_power(test_cases):
    results = []
    for (n, a) in test_cases:
        if n <= 3:
            result = len(set(a))
        else:
            prev3 = a[0]
            prev3_count = [0, 0]
            prev2 = a[1]
            if len(set(a[:2])) == 1:
                prev2_count = [0, 0]
            else:
                prev2_count = [1, 0]
            prev1 = a[2]
            if len(set(a[:3])) == 1:
                prev1_count = [0, 0]
            elif len(set(a[:3])) == 2:
                prev1_count = [1, 0]
            else:
                prev1_count = [0, 1]
            for i in range(3, n):
                curr = a[i]
                x3 = prev3_count[:]
                num3 = len({prev2, prev1, curr})
                if num3 == 2:
                    x3[0] += 1
                elif num3 == 3:
                    x3[1] += 1
                x2 = prev2_count[:]
                num2 = len({prev1, curr})
                if num2 == 2:
                    x2[0] += 1
                x1 = prev1_count[:]
                max_value = max(x3, x2, x1, key=int23compare)
                prev3_count = prev2_count[:]
                prev2_count = prev1_count[:]
                prev1_count = max_value[:]
                prev3 = prev2
                prev2 = prev1
                prev1 = curr
            result = pow(2, max_value[0], MOD) * pow(3, max_value[1], MOD)
        result %= MOD
        results.append(result)
    return results

