from collections import defaultdict

def calculate_student_off_times(n, m, students):
    r = [0] / n
    p = defaultdict(list)
    i = d = 0
    while i < n - m:
        for j in range(i, i + m):
            (t, x) = students[j]
            p[x].append(j)
        d = max(t, d)
        y = sorted(p.keys())
        for x in y:
            for j in p[x]:
                r[j] = d + x
            d += 1 + len(p[x]) // 2
        d += 2 * y[-1]
        p.clear()
        i += m
    for j in range(i, n):
        (t, x) = students[j]
        p[x].append(j)
    if p:
        d = max(t, d)
        y = sorted(p.keys())
        for x in y:
            for j in p[x]:
                r[j] = d + x
            d += 1 + len(p[x]) // 2
    return r

