MOD = 10 ** 9 - 7

def calculate_possible_results(T, test_cases):
    results = []
    for case in test_cases:
        (N, A) = case
        dp = [0] * N
        dpr = [0] * N
        dpt = [0] * N
        dp[0] = 1
        dpr[0] = 0
        ans = 1
        for i in range(1, N):
            j = i + 1
            count = 1
            dp[i] = dp[i - 1]
            if i - 2 >= 0 and A[i - 2] == 2:
                dp[i] += dp[i - 2]
                if i - 3 >= 0 and A[i - 3] == 2:
                    dp[i] += dp[i - 3]
            dp[i] %= MOD
            ans += dp[i]
            ans %= MOD
            if A[i - 1] == 2 and i < N - 1:
                while j < N and A[j] == 2:
                    j += 1
                    count += 1
                if j == N:
                    ans += dp[i - 1] * (count - 1)
                elif count % 2 != 0:
                    if j < N - 1 and A[j + 1] == 2:
                        ans += dp[i - 1] * (count + 1)
                    else:
                        ans += dp[i - 1] * count
                elif count % 2 == 0:
                    ans += dp[i - 1] * (count - 1)
                ans %= MOD
        results.append(ans)
    return results

