    from collections import defaultdict
    g = defaultdict(lambda : None)
    g[0, 0] = 1

    def grundy(w, b):
        if g[w, b] is not None:
            return g[w, b]
        s = set()
        if w:
            s.add(grundy(w - 1, b))
        if b:
            s.add(grundy(w + 1, b - 1))
        for i in range(1, min(w, b) + 1):
            s.add(grundy(w, b - i))
        i = 0
        while i in s:
            i += 1
        g[w, b] = i
        return i
    ans = 0
    for (w, b) in areas:
        ans ^= grundy(w, b)
