from heapq import *

def lemming_battle(battlefield, green, blue):
    hg = [-v for v in green]
    hb = [-v for v in blue]
    heapify(hg)
    heapify(hb)
    while hb or hg:
        (tmp_b, tmp_g) = ([], [])
        for _ in range(min(battlefield, len(hg), len(hb))):
            cmp = heappop(hg) - heappop(hb)
            if cmp < 0:
                tmp_g.append(cmp)
            elif cmp > 0:
                tmp_b.append(-cmp)
        for lem in tmp_b:
            heappush(hb, lem)
        for lem in tmp_g:
            heappush(hg, lem)
    if not hg and (not hb):
        return 'Green and Blue died'
    (winner, lst) = ('Green', hg) if hg else ('Blue', hb)
    survivors = ' '.join((str(-v) for v in sorted(lst, reverse=True)))
    return f'{winner} wins: {survivors}'

