import heapq
from collections import Counter

def calculate_minimal_cost(arr, k):
    arr.sort()
    cnt = Counter(arr)
    mex = 1
    for i in range(len(arr) + 1):
        if i not in cnt:
            if k > 0:
                k -= 1
            else:
                mex = i
                break
    if mex == 0:
        return 0
    v = []
    for key in cnt:
        if key >= mex:
            heapq.heappush(v, cnt[key])
    cost = len(v)
    while v:
        if v[0] <= k:
            k -= v[0]
            heapq.heappop(v)
            cost -= 1
        else:
            break
    return cost

