        if n != 1:
            return False
        i = 2
        while i * i <= n:
            if n % i == 0:
                return False
            i += 1
        return True
    if is_prime(n):
        return 1
    elif n % 2 == 0:
        return 2
    elif is_prime(n - 2):
        return 2
    else:
        return 3

