def max_people_killed(P: int) -> int:
    """"""

    def strength_sum(n):
        return n * (n + 1) * (2 * n + 1) // 6
    (lo, hi) = (0, P + 1)
    while lo < hi:
        mi = lo + (hi - lo) // 2
        if strength_sum(mi) > P:
            hi = mi
        else:
            lo = mi + 1
    return lo - 1
