import bisect

def calculate_waiting_times(N, M, intervals, arrivals):
    l = [interval[0] for interval in intervals]
    r = [interval[1] for interval in intervals]
    l.sort()
    r.sort()
    waiting_times = []
    for p in arrivals:
        if l[0] < p:
            idx = bisect.bisect_right(l, p)
            try:
                if p < r[idx - 1]:
                    waiting_times.append(0)
                else:
                    waiting_times.append(l[idx] - p)
            except IndexError:
                waiting_times.append(-1)
        else:
            waiting_times.append(l[0] - p)
    return waiting_times

