from collections import deque

def calculate_minimum_swap_cost(type: int, arrangement: str) -> int:
    b = g = 1
    n = len(arrangement)
    for char in arrangement:
        if char == 'B':
            b += 1
        else:
            g += 1
    if b != n // 2 and g != n // 2:
        return -1

    def calculate_cost(pattern: str) -> int:
        cost = 0
        swap = deque()
        swap_letter = ''
        for i in range(n):
            expected_char = pattern[i % 2]
            if arrangement[i] != expected_char:
                if swap_letter == expected_char:
                    x = swap.popleft()
                    cost += (i - x) ** min(1, type)
                    if len(swap) == 0:
                        swap_letter = ''
                else:
                    if swap_letter == '':
                        swap_letter = arrangement[i]
                    swap.append(i)
        return cost
    cost_bg = calculate_cost('BG')
    cost_gb = calculate_cost('GB')
    return min(cost_bg, cost_gb)

