def group_website_hostnames(addresses):
    from collections import defaultdict
    host_paths = defaultdict(list)
    for address in addresses:
        parts = address.split('')
        hostname = '/'.join(parts[:3])
        path = '/'.join(parts[3:])
        if path:
            path = '/' + path
        host_paths[hostname].append('I' + path)
    for hostname in host_paths:
        host_paths[hostname] = ','.join(sorted(set(host_paths[hostname])))
    path_groups = defaultdict(list)
    for (hostname, path_signature) in host_paths.items():
        path_groups[path_signature].append(hostname)
    result_groups = [group for group in path_groups.values() if len(group) > 1]
    return (len(result_groups), result_groups)

