import re
from random import sample

def mix_words(input_string):
    if not isinstance(input_string, str):
        return None

    def jumble_word(match):
        word = match.group(1)
        if len(word) < 3:
            return word
        middle = word[1:-1]
        jumbled_middle = ''.join(sample(middle, len(middle)))
        return word[0] + jumbled_middle + word[-1]
    return re.sub('\\b([a-zA-Z][a-zA-Z]{1,}[a-zA-Z])\\b', jumble_word, input_string)

