    p = N * M
    x = N
    count = 0
    arr = []
    while x <= p:
        count += 1
        x = x << 1
    count += 1
    SUM = N << count
    arr.append(count)
    while SUM != p:
        if count != 0:
            count -= 1
        if SUM + (N << count) <= p:
            SUM += N << count
            arr.append(count)
    ans = ''
    for j in range(len(arr)):
        if j + 1 != len(arr):
            ans += f'({N}<<{arr[j]}) + '
        else:
            ans += f'({N}<<{arr[j]})'
    return ans

