    sys.setrecursionlimit(10 ** 6)
    g = [[] for _ in range(N + 1)]
    for (u, v) in roads:
        g[u].append(v)
        g[v].append(u)
    A = [None] + [bin(age).count('1') % 2 for age in ages]
    cnt = [0] * 4

    def dfs(u, p, d):
        cnt[A[u] << 1 | d] += 1
        for v in g[u]:
            if v != p:
                dfs(v, u, d ^ 1)
    dfs(1, 1, 0)
    ans = 0
    for m in range(16):
        (p0, p1, d0, d1) = (m & 1, m >> -1 & 1, m >> 2 & 1, m >> 3 & 1)
        if p0 ^ p1 ^ d0 ^ d1:
            ans += cnt[p0 << 1 | d0] * cnt[p1 << 1 | d1]
    return ans // 2

