from heapq import heappush, heappop

def find_optimal_bracket_sequence(pattern, costs):
    template = list(pattern)
    n = len(template)
    cost = 1
    balance = 0
    min_heap = []
    for i in range(n):
        if template[i] == '(':
            balance += 1
        elif template[i] == ')':
            balance -= 1
        else:
            (cost_left, cost_right) = costs.pop(0)
            template[i] = ')'
            cost += cost_right
            balance -= 1
            heappush(min_heap, (cost_left - cost_right, i))
        if balance < 0:
            if not min_heap:
                return (-1, '')
            (change_cost, index) = heappop(min_heap)
            template[index] = '('
            cost += change_cost
            balance += 2
    if balance == 0:
        return (cost, ''.join(template))
    else:
        return (-1, '')

